extern crate day1;

#[test]
fn it_converts_to_digits() {
  assert_eq!(day1::string_to_digits("112"), [1, 1, 2]);
}

#[test]
fn finds_matching_digits() {
  let list = vec![1, 1, 2, 3, 3, 1];
  assert_eq!(day1::select_matching_digits(&list), [1, 3, 1]);
}

#[test]
fn sums_everything() {
  assert_eq!(day1::part1("112"), 1);
  assert_eq!(day1::part1("1122"), 3);
  assert_eq!(day1::part1("11223"), 3);
  assert_eq!(day1::part1("311223"), 6);
}

#[test]
fn finds_halfway_round_matches() {
  assert_eq!(day1::select_matching_halfway_round(&vec![1, 2, 1, 2]), [1, 2, 1, 2]);
  assert_eq!(day1::select_matching_halfway_round(&vec![1, 2, 2, 1]), []);
  assert_eq!(day1::select_matching_halfway_round(&vec![1, 2, 3, 4, 2, 5]), [2, 2]);
  assert_eq!(day1::select_matching_halfway_round(&vec![1, 2, 1, 3, 1, 4, 1, 5]), [1, 1, 1, 1]);
}

#[test]
fn part1_solution() {
  let input : &str = "";

  assert_eq!(day1::part1(input), 1223);

}


#[test]
fn part2_solution() {
  let input : &str = "";

  assert_eq!(day1::part2(input), 1284);

}
